  const classes = useStyles();

  render (
    <div  className={classes.root} >
    <form className={classes.root} noValidate autoComplete="off">
    

      <TextField required id="standard-required" label="First Name"  />
      <TextField d id="standard-basic" label="Middle Name"  />
      <TextField required id="standard-required" label="Last Name"  />
      <Divider light />
      <TextField
    id="date"
    label="Birthday"
    type="date"
    defaultValue="2017-05-24"
    className={classes.textField}
    InputLabelProps={{
      shrink: true,
    }}
  />
  <Divider light />
  <TextField required id="standard-required" label="Email"  />
      <TextField d id="standard-basic" label="Contact Number"  />
      <Divider/>
      <TextField required id="standard-required" label="Complete Address"   />
    </form>

  </div>
  
  )
} */


class AddEmployee extends Component {
  constructor() {
    super();
    this.state = {
      fname: '',
      mname:'',
      lname: ''
    }
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(e) {
    this.setState({
      [e.target.name]: e.target.value
    });
  }

  handleSubmit(e) {
    e.preventDefault();
    const itemsRef = app.database().ref('users');
    const item = {
      fname: this.state.fname,
      mname: this.state.mname,
      lname: this.state.lname
    }
    itemsRef.push(item);
    this.setState({
      fname: '',
      mname:'',
      lname: ''
    });
  }
  render() {
    return (
      <div className='app'>
        <header>
            <div className='wrapper'>
              <h1>Add Employee</h1>
              
            </div>
        </header>
        <div className='container'>
          <section className='add-item'>
              <form onSubmit={this.handleSubmit}>
              <TextField required id="standard-required" label="First Name"  name="fname" onChange={this.handleChange} value={this.state.fname} />
              <TextField required id="standard-basic" label="Middle Name" name="mname" onChange={this.handleChange} value={this.state.mname} />
              <TextField required id="standard-required" label="Last Name" name="lname" onChange={this.handleChange} value={this.state.lname} />
              <TextField
              id="date"
              label="Birthday"
              type="date"
              name="bday"
              defaultValue="2017-05-24"
              className={classes.textField}
              InputLabelProps={{
                shrink: true,
              }}
            />
              <TextField required id="standard-required" label="Last Name" name="lname" onChange={this.handleChange} value={this.state.lname} />
              <TextField required id="standard-required" label="Last Name" name="lname" onChange={this.handleChange} value={this.state.lname} />
              <TextField required id="standard-required" label="Last Name" name="lname" onChange={this.handleChange} value={this.state.lname} />
                
                <button>Add Item</button>
              </form>
          </section>
         
        </div>
      </div>
    );
  }
}
export default AddEmployee;
