import React, { Component,useState }  from 'react';

import DataGrid from '@material-ui/data-grid';
import { makeStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import Divider from '@material-ui/core/Divider';

import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TablePagination from '@material-ui/core/TablePagination';
import TableRow from '@material-ui/core/TableRow';
import app from '../firebase.js'; 

const columns = [
  { id: 'fname', label: 'First Name', minWidth: 170 },
  { id: 'mname', label: 'Middle Name', minWidth: 100 },
  { id: 'lname', label: 'Last Name', minWidth: 100 },
  {
    id: 'email',
    label: 'Email',
    minWidth: 100,
    align: 'center',
    format: (value) => value.toLocaleString('en-US'),
  },
  {
    id: 'cnum',
    label: 'Contact#',
    minWidth: 100,
    align: 'right',
    format: (value) => value.toLocaleString('en-US'),
  },

  {
    id: 'action',
    label: 'Action',
    minWidth: 100,
    align: 'center',
  }
];



const useStyles = makeStyles((theme) => ({
  root: {
    '& > *': {
      margin: theme.spacing(1),
      width: '25ch',
    },
  },
}));








class EmployeeList extends Component {
 

  constructor() {
    super();
    this.state = {
      fname: '',
      lname: '',
      users: []
    }
    this.handleChange = this.handleChange.bind(this);
    
  }

  handleChange(e){
    this.setState({
      [e.target.name]: e.target.value
    });
  };


  componentDidMount(){
    const UsersRef = app.database().ref('users');
    UsersRef.on('value', (snapshot) => {
      let users = snapshot.val();
      let newState = [];
      for (let userId in users) {
        newState.push({
          id: userId,
          fname: users[userId].fname,
          lname: users[userId].lname
        });
      }
      this.setState({
        users: newState
      });
    });
  };

  removeItem(userId) {
    const itemRef = app.database().ref(`/users/${userId}`);
    itemRef.remove();
  };

  


 
  render() {
    
    return (
      <div className='app'>
        <header>
            <div className='wrapper'>
              <h1>Fun Food Friends</h1>
              
            </div>
        </header>
        <div className='container'>
      
          <section className='display-item'>
              <div className="wrapper">
                <ul>
                  {this.state.users.map((users) => {
                    return (
                      <li key={users.fname}>
                        <h3>LastName: {users.lname}</h3>
                        <p>brought by: {users.fname}
                          <button onClick={() => this.removeItem(users.id)}>Remove Item</button>
                        </p>
                      </li>
                    )
                  })}
                </ul>

                <TableContainer className={useStyles.container}>
                  <Table stickyHeader aria-label="sticky table">
                  <TableHead>
                    <TableRow>
                      {columns.map((column) => (
                        <TableCell
                          key={column.id}
                          align={column.align}
                          style={{ minWidth: column.minWidth }}
                        >
                          {column.label}
                        </TableCell>
                      ))}
                    </TableRow>
                  </TableHead>
                    <TableBody>
                   {/*  {this.state.users.map((users) => {

                    render(){
                      <TableRow hover role="checkbox" tabIndex={-1} key={users.id}>  
                          <TableCell key={column.id} align={column.align}>
                        {column.format && typeof value === 'number' ? column.format(value) : value}
                      </TableCell>
                      </TableRow>

                     
                     
                    )
                  }) */}
                    

                    {this.state.users.map((users) => {
                    return (
                      <TableRow hover role="checkbox" key={users.id}>
                          <TableCell key={columns.id} align={columns.align}>
                          <p>{users.fname}</p>
                          </TableCell>

                          <TableCell key={columns.id} align={columns.align}>
                          <p>MNAME</p>
                          </TableCell>

                          <TableCell key={columns.id} align={columns.align}>
                          <p>{users.lname}</p>
                          </TableCell>

                          <TableCell key={columns.id} align={columns.align}>
                          <p>EMail</p>
                          </TableCell>

                          <TableCell key={columns.id} align={columns.align}>
                          <p>CPNUM</p>
                          </TableCell>

                          <TableCell key={columns.id} align={columns.align}>
                          <button onClick={() => this.removeItem(users.id)}>Remove Item</button>
                          </TableCell>
                        
                          
                     
                      </TableRow>
                    )
                  })} 
                    

                   
                    </TableBody>
                  </Table>
                </TableContainer>
              </div>
          </section>
        </div>
      </div>
    );
  }
}
export default EmployeeList;
