import app from "../firebase";
const db = app.database();
//const db = firebase.ref("/employees");

class TutorialDataService {
  getAll() {
    return app;
  }

  create(tutorial) {
    return app.push(tutorial);
  }

  update(key, value) {
    return app.child(key).update(value);
  }

  delete(key) {
    return app.child(key).remove();
  }

  deleteAll() {
    return app.remove();
  }
}

export default new TutorialDataService();
