import React, { Component }  from 'react';
import { makeStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import Divider from '@material-ui/core/Divider';
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import clsx from 'clsx';

import IconButton from '@material-ui/core/IconButton';
import Input from '@material-ui/core/Input';
import FilledInput from '@material-ui/core/FilledInput';
import OutlinedInput from '@material-ui/core/OutlinedInput';
import InputLabel from '@material-ui/core/InputLabel';
import InputAdornment from '@material-ui/core/InputAdornment';
import FormHelperText from '@material-ui/core/FormHelperText';
import FormControl from '@material-ui/core/FormControl';
import Visibility from '@material-ui/icons/Visibility';
import VisibilityOff from '@material-ui/icons/VisibilityOff';
import app from '../firebase'; 
const useStyles = makeStyles((theme) => ({
  root: {
    '& > *': {
      margin: theme.spacing(1),
      width: '25ch',
    },
  },
}));





class AddEmployee extends Component {
  constructor() {
    super();
    this.state = {
      fname: '',
      mname:'',
      lname: '',
      bday:'',
      email:'',
      cpnum:'',
      address:'',
      password:'',
    }
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(e) {
    this.setState({
      [e.target.name]: e.target.value
    });
  }

  handleSubmit(e) {
    e.preventDefault();
    const itemsRef = app.database().ref('users');
    const item = {
      fname: this.state.fname,
      mname: this.state.mname,
      lname: this.state.lname,
      bday: this.state.bday,
      email: this.state.email,
      cpnum:this.state.cpnum,
      address:this.state.address,
      password:this.state.password
      
    }
    itemsRef.push(item);
    this.setState({
      fname: '',
      mname:'',
      lname: '',
      bday: '',
      email: '',
      cpnum:'',
      address:'',
      password:''
    });
    
  }
  render() {
    return (
      <div className='app'>
        
        <Card>
        <header>
            <div className='wrapper'>
              <h1>Add Employee</h1>
              
            </div>
        </header>
          <CardContent>
              <form onSubmit={this.handleSubmit}>
              <TextField required id="standard-required" label="First Name"  name="fname" onChange={this.handleChange} value={this.state.fname} />
              <TextField required id="standard-basic" label="Middle Name" name="mname" onChange={this.handleChange} value={this.state.mname} />
              <TextField required id="standard-required" label="Last Name" name="lname" onChange={this.handleChange} value={this.state.lname} />
              <Divider/>
              <TextField
              id="date"
              label="Birthday"
              type="date"
              name="bday"
              defaultValue="2017-05-24"
              
              InputLabelProps={{
                shrink: true,
              }}

              onChange={this.handleChange} 
              value={this.state.bday}
            />
             <Divider/>
              
              <TextField required id="standard-required" label="Contact No." name="cpnum" onChange={this.handleChange} value={this.state.cpnum} />
              <Divider/>
              <TextField required id="standard-required" label="Address" name="address" onChange={this.handleChange} value={this.state.address} />
               <Divider/>
               <h4>User Credentials</h4>
               <Divider/> 
               <TextField required id="standard-required" label="Email" name="email" onChange={this.handleChange} value={this.state.email} />
               <TextField required id="standard-required" label="Password" name="password" onChange={this.handleChange} value={this.state.password} />
               <FormControl className={clsx(classes.margin, classes.textField)} variant="filled">
          <InputLabel htmlFor="filled-adornment-password">Password</InputLabel>
          <FilledInput
            id="filled-adornment-password"
            type={values.showPassword ? 'text' : 'password'}
            value={values.password}
            onChange={handleChange('password')}
            endAdornment={
              <InputAdornment position="end">
                <IconButton
                  aria-label="toggle password visibility"
                  onClick={handleClickShowPassword}
                  onMouseDown={handleMouseDownPassword}
                  edge="end"
                >
                  {values.showPassword ? <Visibility /> : <VisibilityOff />}
                </IconButton>
              </InputAdornment>
            }
          />
        </FormControl>
                <button>Add Employee</button>
              </form>
              </CardContent>
         </Card>
       
      </div>
    );
  }
}
export default AddEmployee;
