import firebase from '../firebase';

const db = firebase.databse().ref('/users');
let employee = [];

class EmployeeService {
  
  

  getAll() {
    return db;
  };

  get(key) {
    return db.child(key);
  };

  update(key, value) {
    return db.child(key).update(value);
  };

  delete(key) {
    return db.child(key).remove();
  }
}

export default new EmployeeService();
