import React from 'react';
import { Text, View } from 'react-native';

const AddEmployee = () => {
  return (
    <View
      style={{
        flex: 1,
        justifyContent: "center",
        alignItems: "center"
      }}>
      <Text>Add Employees</Text>
    </View>
  )
}
export default AddEmployee;
